select count(*) from employees;

select sum(salary), avg(salary) from employees;

select round(avg(salary),2) from employees;

select max(salary) MAXSALARY , min(salary) MINSALARY from employees;

select max(last_name), min(last_name) from employees;

select max(hire_date), min(hire_date) from employees;

select round(sum(salary)/count(nvl(manager_id,0)),2) from employees;

select count(department_id) from departments;

select department_id, count(department_id), sum(salary)
from employees
group by department_id
order by department_id;

select count(department_id) from employees; --[to count total no. of employees]

select department_id,count(department_id) --[to count total no. of employees in each department]
from employees
group by department_id 
order by department_id;

select department_id, job_id, count(department_id)
from employees
group by department_id ,job_id
order by department_id;

select department_id, count(department_id)
from employees
group by department_id 
having sum(salary)>15000
order by department_id;


select salary,commission_pct,nvl2(commission_pct,salary,122222222) from employees;



select first_name,last_name,nullif(length(first_name),length(last_name)) from employees;




select salary,commission_pct,nvl2(commission_pct,salary+commission_pct,12222222) from employees;

desc employees;

select commission_pct,manager_id,department_id,coalesce(commission_pct,first_name,10000) from employees;


